import React, { useState } from 'react';

class Register extends React.Component{

    componentDidMount() {
        document.title = 'Login';
    }

    subbmission = (event) => {
        let password =  document.getElementById('account-password').value;
        let account_id =  document.getElementById('account-id').value;

        var xhttp = new XMLHttpRequest();
        var data = [];
        xhttp.onreadystatechange = function() {
            if (this.readyState === 4 && this.status === 200) {
            //Typical action to be performed when the document is ready:
                data = JSON.parse(this.responseText);
                alert(this.responseText);
            }
        };
        xhttp.open("POST", "http://localhost:8000/login/", true);
        xhttp.send("account_password=" + password);
        xhttp.send("account_id=" + account_id);

    }
    render() {
        const form =
            <div className="container-fluid">
                <div className="row mt-5 justify-content-center">
                    <div className="col-5">
                        <div className="login-box">
                            <div className="login-logo">
                                <b>GoVersus</b>
                            </div>

                            <div className="card">
                                <div className="card-body login-card-body">
                                    <p className="login-box-msg">Sign in to start your session</p>

                                    <form action="#" method="post">
                                        <div className="input-group mb-3">
                                            <input type="email" className="form-control" placeholder="Email" />
                                                <div className="input-group-append">
                                                    <div className="input-group-text">
                                                        <span className="fas fa-envelope"></span>
                                                    </div>
                                                </div>
                                        </div>
                                        <div className="input-group mb-3">
                                            <input type="password" className="form-control" placeholder="Password" />
                                                <div className="input-group-append">
                                                    <div className="input-group-text">
                                                        <span className="fas fa-lock"></span>
                                                    </div>
                                                </div>
                                        </div>
                                        <div className="row">
                                            <div className="col-8">
                                                <div className="icheck-primary">
                                                    <input type="checkbox" id="remember" />
                                                        <label htmlFor="remember">
                                                            Remember Me
                                                        </label>
                                                </div>
                                            </div>

                                            <div className="col-4">
                                                <button type="submit" className="btn btn-primary btn-block">Sign In</button>
                                            </div>

                                        </div>
                                    </form>

                                    <div className="social-auth-links text-center mb-3">
                                        <p>- OR -</p>
                                        <a href="#" className="btn btn-block btn-primary">
                                            <i className="fab fa-facebook mr-2"></i> Sign in using Facebook
                                        </a>
                                        <a href="#" className="btn btn-block btn-danger">
                                            <i className="fab fa-google-plus mr-2"></i> Sign in using Google+
                                        </a>
                                    </div>

                                    <p className="mb-1">
                                        <a href="">I forgot my password</a>
                                    </p>
                                    <p className="mb-0">
                                        <a href="" className="text-center">Register a new membership</a>
                                    </p>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        return (form);
    }
}

export default Register;
