import React from 'react'
import UrlBackEnd from "../../../models/UrlBackEnd";
import axios from "axios";


class Profile extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            user: null,
            profileHeader: null
        }


    }

    componentDidMount() {
        if(this.props.user !== null){
            // loading user info
        const token =  localStorage.getItem('GoVersus_token')
        const url = UrlBackEnd + 'token/get/' + token + '/'

        // updating info
        axios.post(url, {})
        .then(response => {
            const user = response.data

            const profileHeader =
                <div className="row ">
                    <div className="col-5">
                        <div className="card card-primary card-outline">
                            <div className="card-body box-profile vh-100">
                                <div className="text-center">
                                    <img className="profile-picture"
                                         src="https://scontent.ftnr5-1.fna.fbcdn.net/v/t39.30808-6/330216732_5989472734477243_5474576909337582263_n.jpg?_nc_cat=108&ccb=1-7&_nc_sid=09cbfe&_nc_eui2=AeHfBz3KxP4-tioDlAchW545emvSJwolMQZ6a9InCiUxBhtX4fGXd40pEGwRuVsdFRt51bZEdh477Wg1ZATME4Fh&_nc_ohc=0H0wPCb2n48AX-pa57A&_nc_ht=scontent.ftnr5-1.fna&oh=00_AfAFA9HSfjSu8YxCjbskKdEWdATVqtOlRG7h3V35Eqfbpg&oe=6403923E"
                                         alt="User profile picture" />
                                </div>

                                <h3 className="profile-username text-center">{ user.first_name } { user.last_name }</h3>

                                <p className="text-muted text-center">Software Engineer</p>

                                <ul className="list-group list-group-unbordered mb-3">
                                    <li className="list-group-item">
                                        <b>Followers</b> <a className="float-right">1,322</a>
                                    </li>
                                    <li className="list-group-item">
                                        <b>Following</b> <a className="float-right">543</a>
                                    </li>
                                    <li className="list-group-item">
                                        <b>Friends</b> <a className="float-right">13,287</a>
                                    </li>
                                </ul>

                                <a href="#" className="btn btn-primary btn-block"><b>Follow</b></a>
                            </div>

                        </div>


                    </div>
                </div>


            this.setState({
                profileHeader: profileHeader
            })



        })
        .catch(error => {
            alert(error.toString())
        });
        }
    }

    render(){
        const profile =
            <div className="container-fluid">
                { this.state.profileHeader }
            </div>
        return (profile)
    }
}

export default Profile;
