import React from 'react';
import Messages from "./Messages";
import MustLoggedInPage from "../../statics/MustLoggedInPage";
import axios from "axios";
import UrlBackEnd from "../../../models/UrlBackEnd";
import UrlFrontEnd from "../../../models/UrlFrontEnd";

class Message extends React.Component{
    constructor() {
        super();
        this.state = {
            errorMessage: '',
            page: ''
        }

        this.changePage = this.changePage.bind(this);

    }

    static getDerivedStateFromProps(props, state) {
        // localStorage.setItem('key', 'value');
        // const value = localStorage.getItem('key');
        // localStorage.removeItem('key');
        // const exists = localStorage.getItem('key') !== null;
        // const data = { name: 'John', age: 30 };
        // localStorage.setItem('user', JSON.stringify(data));
        // const data = JSON.parse(localStorage.getItem('user'));
        // console.log(data.name); // "John"
        // console.log(data.age); // 30

        const token = localStorage.getItem('GoVersus_token')

        if(token == null){

            state.page = <MustLoggedInPage />
        } else {

            // verifying token
            let url = UrlBackEnd + 'check_token/' + token
            axios.get(url, {})
                .then(response => {
                    let result = response.data;

                    if(typeof(result['token']) === 'string'){
                        state.page = <MustLoggedInPage />
                    }
                    else {
                        state.page = <Messages />
                    }
                  })
                  .catch(error => {
                        alert(error.toString())
                  });
        }

    }

    changePage =  (page) => {
        this.setState({
            page: page
        })
    }

    render() {

        return (this.state.page);
    }
}

export default Message;
