import React from 'react'
import UrlBackEnd from "../../../models/UrlBackEnd";
import axios from "axios";


class Profile extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            user: null
        }


    }

    componentDidMount() {
        if(this.props.user !== null){
            // loading user info
        const token =  localStorage.getItem('GoVersus_token')
        const url = UrlBackEnd + 'token/get/' + token + '/'

        // updating info
        axios.post(url, {})
        .then(response => {
            const user = response.data

            const userBar =
            <div className="col-9 text-navbar" >
                <h3 className="pt-2" style={{opacity: '0.5', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                    { user.first_name } { user.last_name }
                </h3>
            </div>

            this.setState({
                userBar: userBar
            })



        })
        .catch(error => {
            alert(error.toString())
        });
        }
    }

    render(){
        const profile =
            <div className="container-fluid">
                <div className="row">


                    <div className="col-md-3">

                        <div className="card card-primary card-outline">
                            <div className="card-body box-profile">
                                <div className="text-center">
                                    <img className="profile-user-img img-fluid img-circle"
                                         src="../../dist/img/user4-128x128.jpg"
                                         alt="User profile picture" />
                                </div>

                                <h3 className="profile-username text-center">Nina Mcintire</h3>

                                <p className="text-muted text-center">Software Engineer</p>

                                <ul className="list-group list-group-unbordered mb-3">
                                    <li className="list-group-item">
                                        <b>Followers</b> <a className="float-right">1,322</a>
                                    </li>
                                    <li className="list-group-item">
                                        <b>Following</b> <a className="float-right">543</a>
                                    </li>
                                    <li className="list-group-item">
                                        <b>Friends</b> <a className="float-right">13,287</a>
                                    </li>
                                </ul>

                                <a href="#" className="btn btn-primary btn-block"><b>Follow</b></a>
                            </div>

                        </div>


                    </div>
                </div>
            </div>
        return (profile)
    }
}

export default Profile;
