import React from 'react';
import axios from "axios";
import UrlBackEnd from "../../../models/UrlBackEnd";
import UrlFrontEnd from "../../../models/UrlFrontEnd";

class Messages extends React.Component{
    constructor(props) {
        super();
        this.state = {
            errorMessage: '',
            token: props.token,
            messagesList: null,
            messagesData: null,
            messagesHeader: null,
            messagesContent: null,
            conversation: null
        }

        this.getMessages = this.getMessages.bind(this);

    }

    componentDidMount() {
        let url = UrlBackEnd + 'messages/' + this.state.token + '/'


        axios.post(url, {})
        .then(response => {
            let data = response.data;

            const messagesList = data.map((content)  => (

                <div className="row mt-1 justify-content-center" key={content.id} onClick={() => this.getMessages(content.id)}>
                            <div className="col-12">
                                <div className="row pt-2 pb-2 navbar-element navbar-profile">
                                    <div className="col-2 text-center">
                                        <span className="" style={{fontSize: '170%'}}>
                                            <img className="small-image-50 rounded-circle border border-dark"
                                                 src="../../static/images/profile_pictures/eudo.png" alt="Eudoxie"/>
                                        </span>
                                    </div>
                                    <div className="col-10 pl-4 text-navbar">
                                        <div className="row">
                                            <div className="col-7 mt-1" style={{ whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>

                                                {content.destinator['first_name']}


                                                <div className="h-50 overflow-auto"
                                                     style={{ whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}
                                                >
                                                    <p style={{ opacity : '0.6', display: 'inline'}}>
                                                        {content.last_message['content']}
                                                    </p>
                                                </div>

                                            </div>
                                            <div className="col-4 mt-1 ml-2">
                                                <span style={{ fontSize: '12px' }}>
                                                     {content.last_message['date'].slice(11, 16)}
                                                </span>
                                                <br />
                                                <span style={{ fontSize: '9px' }}>
                                                    {content.last_message['date'].slice(0, 10)}
                                                </span>

                                            </div>
                                        </div>

                                    </div>
                                </div>
                            </div>
                    </div>

            ))

            this.setState({
                messagesList : messagesList,
                messagesData: data
            })


        })
        .catch(error => {
        alert(error.toString())
        });
    }


    getMessages =  (id_message) => {
        const message = this.state.messagesData[id_message]

        // header changed
        const header =
                    <div className="row pl-1 pt-1 pb-1 border-bottom">
                            <div className="col-1 text-center">
                                <span className="" style={{fontSize: '170%'}}>
                                    <img className="small-image-50 rounded-circle border border-dark"
                                         src="../../static/images/profile_pictures/eudo.png" alt="Eudoxie"
                                    />
                                </span>
                            </div>
                            <div className="col-9 text-navbar" style={{cursor: 'pointer'}}>
                                <h3 className="pt-2 pl-3">
                                    { message.destinator['first_name'] } { message.destinator['last_name'] }
                                </h3>
                            </div>
                            <div className="col-2 mt-2 text-center text-navbar">
                                <span className="fas fa-info-circle" style={{fontSize: '170%', cursor: 'pointer'}}></span>
                            </div>
                    </div>

        // message changed
        let conversation = [];

        // Convertir les objets Message en liste de dictionnaires
        let content = message['message']
        for (let i = 0; i < content.length; i++) {
              let conversationDict = {};
              conversationDict.id = content[i].id;
              conversationDict.content = content[i].content;
              conversationDict.date = content[i].date;
              conversationDict.destinator = content[i].destinator.id;
              conversationDict.expeditor = content[i].expeditor.id;
             conversation.push(conversationDict);
        }


        const conversationView = conversation.map((content)  => (
            <div className="row pt-2 ">
                                        <div className="col-2 text-center">
                                            <span className="" style={{fontSize: '100%'}}>
                                                <img className="small-image-50 rounded-circle border border-dark"
                                                     src="../../static/images/profile_pictures/eudo.png" alt="Eudoxie"/>
                                            </span>
                                        </div>
                                        <div className="col-10 text-navbar">
                                            <div className="row  h-auto border border-dark border-radius-15 p-1" style={{maxWidth : '90%'}}>
                                                <div className="col-12" style={{wordWrap: 'break-word' }}>
                                                    <p style={{ display: 'inline'}}>
                                                        {content.content}
                                                    </p>


                                                </div>
                                            </div>
                                            <div className="row mt-1">
                                                <div className="col-12">
                                                    <span style={{ fontSize: '12px' }}>
                                                         17:03 01/03/23 {content.id }
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                    </div>

        ))

        this.setState({
            messagesHeader: header,
            conversation: conversationView
        })

    }

    render() {

        const page =
            <div className="row">
                <div className="col-4 border-left" style={{height: "88.5vh"}}>

                    { this.state.messagesList }

                </div>

                <div className='col-8 border-left border-dark pl-3'>

                    { this.state.messagesHeader }


                    <div className="row" style={{ height: '90%'}}>
                        <div className="col-12">

