import React, { useState } from 'react';


class Login extends React.Component{

    componentDidMount() {
        document.title = 'Login';
    }

    // subbmission = (event) => {
    //     let password =  document.getElementById('account-password').value;
    //     let account_id =  document.getElementById('account-id').value;

    //     var xhttp = new XMLHttpRequest();
    //     var data = [];
    //     xhttp.onreadystatechange = function() {
    //         if (this.readyState === 4 && this.status === 200) {
    //         // Typical action to be performed when the document is ready:
    //             data = JSON.parse(this.responseText);
    //             alert(this.responseText);
    //         }
    //     };
    //     xhttp.open("POST", "http://localhost:8000/login/", true);
    //     xhttp.send("account_password=" + password);

    // }

    //Controller
    PostData = (event) =>
    {
        let password =  document.getElementById('account-password').value
        let account_id =  document.getElementById('account-id').value
        let data = {}
        data.password = password
        data.account_id = account_id
        data = JSON.stringify(data)

        console.log(data)

    }

    render() {
        const form =
            <div className="form">
                <div className="login-content">
                    <div className="logo-goVersus">
                        <img src={process.env.PUBLIC_URL + "/static/view/img/global/logoGoVersus.png"} alt=""/>
                    </div>
                    <form method="post">
                        <div className="login form-group">
                            <label htmlFor="account-id" className="form-text text-muted"><img src={process.env.PUBLIC_URL + "/static/view/img/login/account.png"} className="ico account-ico"/> Enter your email or your phone number</label>
                            <input type="text" id="account-id" className="login-input form-control" name="account-id" placeholder="Email or phone"/>

                            <div className="id-error"></div>

                            <label htmlFor="account-password" className="form-text text-muted"><img src={process.env.PUBLIC_URL + "/static/view/img/login/password.png"} className="ico password-ico"/>Enter your password</label>
                            <input type="password" id="account-password" className="login-input form-control" name="account-password" placeholder="Password"/>

                            <div className="password-error"></div>
                            <input type="button" onClick={this.PostData} id="submit" value="login" className="login-submit"/>
                        </div>
                    </form>
                    <div className="other-method">
                        <h1 className="form-text text-muted">OR</h1>
                        <p className="form-text text-muted">log with</p>

                        <p>
                        <a href="">
                            <img src={process.env.PUBLIC_URL + "/static/view/img/login/facebook.png"}/>Facebook
                        </a>
                        </p>
                        <p>
                        <a href="">
                            <img src={process.env.PUBLIC_URL + "/static/view/img/login/google.png"}/>Google
                        </a>
                        </p>
                    </div>
                    <div className="signin-link">
                        <a href="">Did not have account ? Click here for Sign-In</a>
                    </div>
            </div>
            <script src="./view/js/login/login.js"> </script>
            </div>

        return (form);
    }
}

export default Login;
